import { useState } from "react";

const DiaryEditor = () => {

    const [state, setState] = useState({
        author: '',
        content: '',
    })

    return(
    <div className="DiaryEditor">
        <h2>오늘의 일기</h2>
        <div>
            <input 
                value={state.authorauthor} 
                onChange={(e)=>{
                    setState.author(e.target.value);
                }}/>
        </div>
        <div>
            <textarea 
                value={state.content} 
                onChange={(e)=>{
                    setState.content(e.target.value);
                }}/>
        </div>
    </div>
    )
}

export default DiaryEditor;
